import React, { Component } from 'react';

import ReactApexChart from 'react-apexcharts';

class AreaChart extends Component {
	constructor(props) {
		super(props);

		this.state = {
			series: [
				{
					name: 'Exercise',
					data: [31, 40, 28, 51, 42, 109, 100],
				},
				{
					name: 'Studying',
					data: [11, 32, 45, 32, 34, 52, 41],
				},
				{
					name: 'Working',
					data: [13, 30, 45, 3, 34, 23, 41],
				},
				{
					name: 'Eating',
					data: [21, 32, 4, 32, 54, 52, 21],
				},
				{
					name: 'Resting',
					data: [10, 39, 45, 32, 34, 52, 31],
				},
				{
					name: 'Sleeping',
					data: [5, 32, 42, 20, 44, 52, 81],
				},
				{
					name: 'General',
					data: [51, 32, 5, 36, 54, 32, 11],
				},
			],
			options: {
				chart: {
					height: 350,
					type: 'area',
				},
				dataLabels: {
					enabled: false,
				},
				stroke: {
					curve: 'smooth',
				},
				xaxis: {
					type: 'datetime',
					categories: [
						'2018-09-19',
						'2018-10-19T01:00:00.000Z',
						'2018-11-19T02:30:00.000Z',
						'2018-12-19T03:30:00.000Z',
						'2018-09-19T04:30:00.000Z',
						'2018-09-19T05:30:00.000Z',
						'2018-09-19T06:30:00.000Z',
					],
				},
				tooltip: {
					x: {
						format: 'dd/MM/yy HH:mm',
					},
				},
			},
		};
	}

	render() {
		return (
			<div id='chart'>
				<ReactApexChart
					options={this.state.options}
					series={this.state.series}
					type='area'
					height={350}
				/>
			</div>
		);
	}

	generateDayWiseTimeSeries = (baseval, count, yrange) => {
		var i = 0;
		var series = [];
		while (i < count) {
			var x = baseval;
			var y = Math.floor(Math.random() * (yrange.max - yrange.min + 1)) + yrange.min;

			series.push([x, y]);
			baseval += 86400000;
			i++;
		}
		return series;
	};

	render() {
		return (
			<div id='chart'>
				<ReactApexChart
					options={this.state.options}
					series={this.state.series}
					type='area'
					height={350}
				/>
			</div>
		);
	}
}

export default AreaChart;
